import React from "react";
import styled from "styled-components";

const Button = () => {
  return (
    <>
      <p style={{ textAlign: "center" }}>Buttons Component</p>
      <BtnWrapper>Css transition</BtnWrapper>
      <DivWrapper>Move with Ease</DivWrapper>
    </>
  );
};

export default Button;

const BtnWrapper = styled.button`
  height: 90px;
  width: 290px;
  font-size: 28px;
  color: white;
  border: none;
  background-color: blue;
  transition-property: background-color;
  transition-duration: 2s; //we can use millisecond ex: 2000ms is the same

  &:hover {
    background-color: green;
  }
`;

const DivWrapper = styled.div`
  width: 150px;
  height: 50px;
  padding: 10px 15px;
  background-color: red;
  color: white;
  text-align: center;
  border-radius: 15px;
  line-height: 45px;
  // responsabile for changing the location of tag
  transition-property: translate();
  transition-duration: 3s;
`;
